Scripts.OnMouseDown:
 				if ( arg1 == "LeftButton" ) then
 					self:StartMoving();
 				elseif (arg1 == "RightButton") then
					InsightOptionsDisplay:SetPoint("TOPLEFT", "UIParent", "TOP", -192, -140);
 				end

Scripts.OnUpdate:
if (InsightOptionsDisplayMoveTarget:GetChecked() == 1) then
	InsightOptionsDisplayTargetFrameVertPosTemp = 0 - InsightOptionsDisplayTargetFrameVertPos:GetValue();
	TargetFrame:SetPoint("TOPLEFT", "UIParent", "TOPLEFT", InsightOptionsDisplayTargetFramePos:GetValue(), InsightOptionsDisplayTargetFrameVertPosTemp);
else
	TargetFrame:SetPoint("TOPLEFT", "UIParent", "TOPLEFT", 250, -4);
end
