Scripts.OnMouseDown:
 				if ( arg1 == "LeftButton" ) then
 					self:StartMoving();
 				elseif (arg1 == "RightButton") then
					JDHPOptionsDisplay:SetPoint("TOPLEFT", "UIParent", "TOP", -192, -140);
 				end

Scripts.OnUpdate:
if (JDHPOptionsDisplayMoveTarget:GetChecked() == 1) then
	JDHPOptionsDisplayTargetFrameVertPosTemp = 0 - JDHPOptionsDisplayTargetFrameVertPos:GetValue();
	TargetFrame:SetPoint("TOPLEFT", "UIParent", "TOPLEFT", JDHPOptionsDisplayTargetFramePos:GetValue(), JDHPOptionsDisplayTargetFrameVertPosTemp);
else
	TargetFrame:SetPoint("TOPLEFT", "UIParent", "TOPLEFT", 250, -4);
end

Scripts.OnValueChanged:
if (JDHPOptionsDisplayMoveTarget:GetChecked() == 1) then
	JDHPOptionsDisplayTargetFrameVertPosTemp = 0 - JDHPOptionsDisplayTargetFrameVertPos:GetValue();
	TargetFrame:SetPoint("TOPLEFT", "UIParent", "TOPLEFT", JDHPOptionsDisplayTargetFramePos:GetValue(), JDHPOptionsDisplayTargetFrameVertPosTemp);
end
JDHPOptionsDisplayTargetFrameVertPosTemp = 0 - JDHPOptionsDisplayTargetFrameVertPos:GetValue();
JDHPOptionsDisplayTargetFrameVertPosCurr:SetText(JDHPOptionsDisplayTargetFrameVertPosTemp);
